fa = "ATAGCCACATCCCTGAATGTCACCTGTCCCTGGGTGAGAGCCATGCCTGACTTGTCTTTCTTTCCTCTTCCTCTTCCGGCGCGGGCG"
def find_substring_positions(string, substring):
    positions = []
    start = 0
    while True:
        start = string.find(substring, start)
        if start == -1:
            break
        positions.append(start)
        start += 1  # Move to the next character after the current found substring
    return positions

